// program to enable LD2 (LED2) on NUCLEO-STM32F446RE

#include <stdint.h>

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

int main(void)
{


	uint32_t* p_clkCtrlReg = (uint32_t*) 0x40023830;
	uint32_t* p_portAModeReg = (uint32_t*) 0x40020000;
	uint32_t* p_portAOutReg = (uint32_t*) 0x40020014;

	// enable clock for GPIOA peripheral in the AHB1ENR
	*p_clkCtrlReg |= 0x1;

	// configure the mode of IO pin as output
	// clear 10th and 11th bits
	// 3 is same as 11 in binary (to clear both bits 10 and 11)
	*p_portAModeReg &= ~(3 << 10);

	// make 11 bit position as 1 (SET)
	*p_portAModeReg |= 0x00000400;

	// SET 12th bit of output data reg to make IO pin-5 as HIGH
	*p_portAOutReg |= (1 << 5);

    /* Loop forever */
 	while(1);
}
